import { Component, EventEmitter, Input, Output } from '@angular/core';
import { SuperHero } from 'src/app/models/super-hero';
import { SuperHeroService } from 'src/app/services/super-hero.service';

@Component({
  selector: 'app-edit-hero',
  templateUrl: './edit-hero.component.html',
  styleUrls: ['./edit-hero.component.css']
})

export class EditHeroComponent {
  @Input() hero?: SuperHero;
  @Output() heroesUpdated = new EventEmitter<SuperHero[]>();

  constructor(private superHeroService : SuperHeroService)

  updateHero(hero:SuperHero){
    this.superHeroService
    .updateHero(hero)
    .subscribe((heroes: SuperHero[]) => this.heroesUpdated.emit(heroes))
  }

  deleteHero(hero:SuperHero){
    this.superHeroService
    .deleteSuperHero(hero)
    .subscribe((heroes: SuperHero[]) => this.heroesUpdated.emit(heroes))
  }
  createHero(hero:SuperHero){
    this.superHeroService
    .createHero(hero)
    .subscribe((heroes: SuperHero[]) => this.heroesUpdated.emit(heroes))
  }
}
<div *ngIf = "hero">
    <h2>{{ hero.name }}</h2>
    <div>
        Name:
        <input [(ngModel)]="hero.name" placeholder="Name"/>
    </div>
    <div>
        First Name:
        <input [(ngModel)]="hero.firstName" placeholder="FirstName"/>
    </div>
    <div>
        Last Name:
        <input [(ngModel)]="hero.lastName" placeholder="LastName"/>
    </div>
    <div>
        Place:
        <input [(ngModel)]="hero.place" placeholder="Place"/>
    </div>

    <button (click)="updateHero(hero)" *ngIf="hero.id">Save</button>
    <button (click)="deleteHero(hero)" *ngIf="hero.id">Delete</button>
    <button (click)="createHero(hero)" *ngIf="!hero.id">Create</button>
</div>
